import React from 'react'
import '../styles/tour-deatails.css';
import { Container, Row,Col,Form,ListGroup} from 'reactstrap';
import {useParams} from 'react-router-dom';
import tourData from '../assets/data/tours';
import calculateAvgRating from './../utils/avgRating';

const TourDetails = () => {

   const {id} = useParams()
   const tour=tourData.find(tour=>tour.id===id)
   
   const {photo,title,desc,price,address,reviews,city,distance,maxGroupSize}=tour;
   const {totalRating,avgRating} =calculateAvgRating(reviews)

  return (
    <>
    <section>
      <Container>
        <Row>
          <Col lg='8'>
           <div className="tour__content">
            <img src={photo} alt="" />

            <div className="tour__info">
              <h2>{title}</h2>

              <div className='d-flex align-itms-center gap-5'>
                <span className="tour__rating d-flex align-items-center gap-1">
                      <i className="ri-star-fill" style={{'color':'var(--secondary-color)'}}></i>{avgRating !== 0 && avgRating}
                      {totalRating === 0 ? 'Not rated' : (
                  <span>({reviews?.length})</span>
              )}
                </span>
                
                <span>
                  <i class="ri-map-pin-user-fill"></i>{address}
                </span>
              </div>

              <div>
                <span>
                <i class="ri-map-pin-2-fill"></i>{city}
                </span>
                <span>
                <i class="ri-money-rupee-circle-line"></i>Rs.{price}
                </span>
                <span>
                <i class="ri-group-line"></i>{maxGroupSize}
                </span>
              </div>
            </div>
           </div>
          </Col>
        </Row>
      </Container>
    </section>
    </>
  )
}

export default TourDetails;
